                  "properties": {
                    "locationName": {
                      "maxLength": 100,
                      "type": "string",
                      "description": "The name of the location."
                    },
                    "UNLocationCode": {
                      "maxLength": 5,
                      "type": "string",
                      "description": "The UN Location code specifying where the place is located."
                    },
                    "facilityCode": {
                      "maxLength": 6,
