                  }
                },
                {
                  "required": [
                    "UNLocationCode"
                  ],
                  "type": "object",
                  "description": "An interface used to express a location using a `Un Location Code`\n",
                  "properties": {
                    "locationName": {
                      "maxLength": 100,
                      "type": "string",
                      "description": "The name of the location."
                    },
                    "UNLocationCode": {
                      "maxLength": 5,
                      "type": "string",
                      "description": "The UN Location code specifying where the place is located."
