                "enum": [
                  "FF",
                  "SI",
                  "PO",
                  "CR",
                  "AAO",
                  "ECR",
                  "CSI",
                  "BPR",
                  "BID",
                  "EQ",
                  "RUC",
                  "DUE",
                  "CER",
                  "AES"
                ]
              },
              "value": {
                "maxLength": 100,
                "type": "string",
                "description": "The actual value of the reference."
              }
            }
          }
        }
